mapper.select:
SELECT h.hotid,h.hotcar,h.hottype,h.createTime,h.deflg,v.sellPrice FROM 
`hotvehicle` h LEFT JOIN(SELECT carName,sellPrice FROM `vehicledet` GROUP BY carName ORDER BY carId) v
on h.hotcar=v.carName


		and h.hotid=#{hotid}


		and h.hotid like '%${keyword}%'


		or h.hotcar like '%${keyword}%'


		or v.sellPrice like '%${keyword}%'



	ORDER BY ${sidx} ${order}


		limit #{start},#{limit}

SELECT hotid,hotcar,hottype FROM `hotvehicle`


		and hotid=#{hotid}


limit 1

SELECT vehId,carName,classify FROM `vehiclebanner` 


		and carName=#{carName}


		and classify=#{classify}


GROUP BY carName ORDER BY vehId

SELECT vehId,picAddress,isShow FROM `vehbannerpic`


		and vehId=#{vehId}


		and isShow=#{isShow}


GROUP BY vehId LIMIT 1
