SELECT sr.role_id roleid,sr.role_name rolename, su.user_id userid,su.shopid,su.shopname,su.username,su.uname,su.portait,su.email, su.mobile,su.`status`,su.dept_id udeptid,su.create_time createtime FROM `sys_role` sr LEFT JOIN sys_user su on sr.dept_id=su.dept_id
and su.dept_id in
#{item}
or su.shopname like '%${keyword}%'
or su.username like '%${keyword}%'
or su.uname like '%${keyword}%'
or su.mobile like '%${keyword}%'
ORDER BY ${sidx} ${order}
limit #{start},#{limit}

SELECT dept_id deptid,`name` FROM `sys_dept`


		and dept_id=#{udeptid}


LIMIT 1
